<?php

declare(strict_types=1);

use Domain\SecureShell\Rules\SecureShellKey;

it('can pass validation', function () {
    $subject = new SecureShellKey();

    expect($subject->passes('public_key', 'ssh-rsa  schacon@mylaptop.local'))
        ->toBeTrue();
    expect($subject->passes('public_key', 'ssh-ed25519 AAAAC3NzaC1lZDI1NTE5AAAAIAgeiLUVQta7ID6QcOC38g9DXjecsajj84EtIK9+BQgi dummy'))->toBeTrue();
});

it('can fail validation', function () {
    $subject = new SecureShellKey();

    $invalidKey = base64_encode(str_replace('ssh-rsa', 'ssh-dsa', base64_decode('', true)));

    expect($subject->passes('public_key', 'ssh-rsa ****invalid$$$ schacon@mylaptop.local'))->toBeFalse();
    expect($subject->passes('public_key', "ssh-rsa {$invalidKey} schacon@mylaptop.local"))->toBeFalse();
    expect($subject->passes('public_key', 'ssh-bfs  schacon@mylaptop.local'))
        ->toBeFalse();
    expect($subject->passes('public_key', 'invalid'))->toBeFalse();
});

it('shows the correct validation message', function () {
    $subject = new SecureShellKey();

    expect($subject->message())->toBe(trans('validation.messages.secure_shell_key'));
});
